import React from 'react';

import './App.css';
import Posts from './components/Posts'



 class App extends React.Component {

  state = {
    posts: []
  }

  componentWillMount() {
    fetch('https://jsonplaceholder.typicode.com/posts')
    .then(res => res.json())
    .then(data => this.setState({posts: data}));
  }

  render() {
    return (
      <div className="App">
        <Posts />
      </div>
    );
  }
}


export default App;
